import { Box, Typography, Button, Stack } from '@mui/material';
import { styled } from '@mui/system';

// Splash Screen için ana bileşen
const SplashScreen = styled(Box)(({ theme }) => ({
    height: '100vh',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#008CBA',
}));

// BÜŞRA HOME logosu için bileşen
const Logo = styled('img')(({ theme }) => ({
    width: '200px',
    marginBottom: '32px',
}));

// Açılış mesajı için bileşen
const SplashMessage = styled(Typography)(({ theme }) => ({
    color: 'white',
    textAlign: 'center',
    marginBottom: '32px',
}));

// Düğme için bileşen
const SplashButton = styled(Button)(({ theme }) => ({
    color: 'white',
    backgroundColor: '#F8B195',
    '&:hover': {
        backgroundColor: '#f69c4f',
    },
}));

// Splash Screen bileşenlerini bir araya getirme
export const HomeScreen = () => {
    return (
        <SplashScreen>
            <Logo src="https://i.imgur.com/Lz9XSPJ.png" alt="BÜŞRA HOME" />
            <SplashMessage variant="h4">
                Hoşgeldiniz, aradığınız her şey burada!
            </SplashMessage>
            <Stack direction="row" spacing={2}>
                <SplashButton variant="contained">Ürünler</SplashButton>
                <SplashButton variant="outlined">Hakkımızda</SplashButton>
            </Stack>
        </SplashScreen>
    );
}
