import * as React from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Grid from "@mui/material/Grid";
import { toast } from "react-hot-toast";
import { addProductToCart, createCart } from "../api/CartService";
import {CardMedia} from "@mui/material";

const images = [
    "https://media.bite.lt/@bite-lt/sites/default/files/products/2021-04/iphone_12_purple-3_1.png",
    "https://reimg-teknosa-cloud-prod.mncdn.com/mnresize/600/600/productimage/125077974/125077974_0_MC/51791131.png",
    "https://static.ticimax.cloud/37071/uploads/urunresimleri/buyuk/elma-kirmizi-isparta-4419.jpeg"
];

const card = (product, image) => (
    <React.Fragment>
        <CardContent>
            <Typography variant="h5" component="div">
                {product.productName}
            </Typography>
            <Typography sx={{ mb: 1.5 }} color="text.secondary">
                <img src={image} alt={product.productName} />
            </Typography>
            <Typography variant="body2">
                {product.salesPrice} TL
            </Typography>
        </CardContent>
        <CardActions sx={{ justifyContent: "center", marginBottom: 1 }}>
            <Button
                onClick={() => {
                    addProduct(product.productId);
                    toast.success('Product successfully added to Cart')
                }}
                size="small"
                variant='contained'
                sx={{ justifyContent: "center", background: '#4d72eb' }}
            >
                Add to Cart
            </Button>
        </CardActions>
    </React.Fragment>
);



const addProduct = (productId) => {
    if (localStorage.getItem('cartId') == null) {
        createCart().then((res => {
            localStorage.setItem("cartId", res);
        }))
    }
    const cartId = localStorage.getItem('cartId');
    console.log(cartId);
    addProductToCart(cartId, productId).then((res) => console.log("eklendi"));
}

export const Products = ({ products }) => {
    return (
        <Grid container spacing={2}>
            {products?.map((product, index) => {
                return (
                    <Grid item>
                        <Card sx={{ minWidth: 275 }} variant="outlined">
                            {card(product, index)}
                        </Card>
                    </Grid>
                );
            })}
        </Grid>
    );
};
