import * as React from 'react';
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import CssBaseline from '@mui/material/CssBaseline';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import List from '@mui/material/List';
import Typography from '@mui/material/Typography';
import ListItem from '@mui/material/ListItem';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import IconButton from "@mui/material/IconButton";
import AddShoppingCartIcon from "@mui/icons-material/AddShoppingCart";
import MenuIcon from '@mui/icons-material/Menu';
import {useEffect, useState} from "react";
import {useLocation, useNavigate} from "react-router-dom";
import SellIcon from '@mui/icons-material/Sell';
import {getCategories} from "../api/CategoryService";
import {getCartProducts} from "../api/CartService";
import {Products} from "./Products";
import {getProducts} from "../api/ProductsService";
import {Checkout} from "./Checkout";
import {ListSubheader} from "@mui/material";



export const CategoryList = () => {

    const navigate = useNavigate()
    const location = useLocation()
    const [categories, setCategories] = useState([])
    const [selectedCategoryId, setSelectedCategoryId] = useState(0)
    const [products, setProducts] = useState([]);
    const [cartProducts, setCartProducts] = useState([]);
    const [open, setOpen] = useState(false);

    const handleDrawerToggle = () => {
        setOpen(!open);
    };



    useEffect(() => {
        getCategories().then((res) =>
        {
            setCategories(res)
        });
    },[])

    useEffect(() => {
        getProducts(selectedCategoryId).then((res) => {
            setProducts(res)
        })
    },[selectedCategoryId]);

    useEffect(() => {
        const cartId = localStorage.getItem('cartId');
        if(cartId){
            getCartProducts(cartId).then((res) => {
                setCartProducts(res)
            })
        }
    },[localStorage.getItem('cartId')])


    return (
        <Box sx={{ display: 'flex' }}>
            <CssBaseline />
            <AppBar
                position="fixed"
                sx={{ background: '#4d72eb' }}
            >
                <Toolbar>
                    <IconButton
                        color="inherit"
                        aria-label="menu"
                        edge="start"
                        onClick={handleDrawerToggle}
                    >
                        <MenuIcon />
                    </IconButton>
                    <Typography
                        variant="h6"
                        noWrap
                        component="div"
                        sx={{ display: { xs: 'none', sm: 'block' } }}
                    >
                        E-Commerce
                    </Typography>
                    <Box sx={{ flexGrow: 1 }} />
                    {location.pathname === '/' && (
                        <Box sx={{ display: { xs: 'none', md: 'flex' } }}>
                            <IconButton
                                onClick={() => navigate('/checkout')}
                                size="large"
                                color="inherit"
                            >
                                <AddShoppingCartIcon />
                            </IconButton>
                        </Box>
                    )}
                </Toolbar>
            </AppBar>
            <Drawer
                anchor="left"
                open={open}
                onClose={handleDrawerToggle}
                ModalProps={{ keepMounted: true }}
                sx={{
                    display: { xs: 'block', md: 'none' },
                    '& .MuiDrawer-paper': {
                        boxSizing: 'border-box',
                        width: 100,
                    },
                }}
            >
                <List sx={{ width: '100%', bgcolor: 'background.paper' }}>
                    {categories.map((category) => (
                        <ListItem
                            button
                            key={category.categoryId}
                            selected={selectedCategoryId === category.categoryId}
                            onClick={() => {
                                setSelectedCategoryId(category.categoryId);
                                handleDrawerToggle();
                            }}
                        >
                            <ListItemText primary={category.categoryName} />
                        </ListItem>
                    ))}
                </List>
            </Drawer>

            <Box
                component="main"
                sx={{ flexGrow: 1, bgcolor: 'background.default', p: 3 }}
            >
                <Toolbar />
                {selectedCategoryId !== null ? (
                    <Box>
                        <Typography variant="h4" mb={3}>
                            {categories.find(
                                (category) => category.categoryId === selectedCategoryId
                            ).categoryName}
                        </Typography>
                        <List sx={{ display: 'flex', flexWrap: 'wrap' }} disablePadding>
                            {categories
                                .find(
                                    (category) => category.categoryId === selectedCategoryId
                                )
                                .products.map((product) => (
                                    <ListItem
                                        key={product.productId}
                                        sx={{ mr: 2, mb: 2 }}
                                        button
                                    >
                                        <Typography variant="h6">{product.productName}</Typography>
                                    </ListItem>
                                ))}
                        </List>
                    </Box>
                ) : (
                    <Box>
                        <Typography variant="h4" mb={3}>
                            Kategoriler
                        </Typography>
                            <List
                                sx={{ display: 'flex', flexWrap: 'wrap' }}
                                disablePadding
                            >
                                {categories.map((category) => (
                                    <ListItem
                                        key={category.categoryId}
                                        sx={{ mr: 2, mb: 2 }}
                                        button
                                        selected={selectedCategoryId === category.categoryId}
                                        onClick={() => setSelectedCategoryId(category.categoryId)}
                                    >
                                        <Typography variant="h6">
                                            {category.categoryName}
                                        </Typography>
                                    </ListItem>
                                ))}
                            </List>
                        </Box>
                )}
                {location.pathname === '/' && (
                    <Products products={products} />
                )}
                {location.pathname === '/checkout' && (
                        <Checkout cartProducts={cartProducts} />
                    )
                }
            </Box>
        </Box>
    );

}
