import * as React from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Grid from "@mui/material/Grid";
import { toast } from "react-hot-toast";
import { addProductToCart, createCart } from "../api/CartService";

const images = [
    "https://picsum.photos/200/300",
    "https://picsum.photos/200/300",
    "https://picsum.photos/200/300"
];

const card = (product, index) => (
    <React.Fragment>
        <CardMedia component="img" height="140" image={images[index]} />
        <CardContent>
            <Typography variant="h5" component="div">
                {product.productName}
            </Typography>
            <Typography sx={{ mb: 1.5 }} color="text.secondary"></Typography>
            <Typography variant="body2">{product.salesPrice} TL</Typography>
        </CardContent>
        <CardActions sx={{ justifyContent: "center", marginBottom: 1 }}>
            <Button
                onClick={() => {
                    addProduct(product.productId);
                    toast.success("Product successfully added to Cart");
                }}
                size="small"
                variant="contained"
                sx={{ justifyContent: "center", background: "#4d72eb" }}
            >
                Add to Cart
            </Button>
        </CardActions>
    </React.Fragment>
);


const addProduct = (productId) => {
    if (localStorage.getItem('cartId') == null) {
        createCart().then((res => {
            localStorage.setItem("cartId", res);
        }))
    }
    const cartId = localStorage.getItem('cartId');
    console.log(cartId);
    addProductToCart(cartId, productId).then((res) => console.log("eklendi"));
}

export const Products = ({ products }) => {
    return (
        <Grid container spacing={2}>
            {
                products?.map((product) => {
                    return (
                        <Grid item>
                            <Card sx={{ minWidth: 275 }} variant="outlined">{card(product)}

                            </Card>

                        </Grid>
                    )
                })
            }
        </Grid>
    );
}
