import * as React from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Grid from "@mui/material/Grid";
import {toast} from "react-hot-toast";
import {addProductToCart, createCart} from "../api/CartService";

const images = [
    'https://images.unsplash.com/photo-1551963831-b3b1ca40c98e',
    'https://images.unsplash.com/photo-1551782450-a2132b4ba21d',
    'https://images.unsplash.com/photo-1522770179533-24471fcdba45',
    'https://images.unsplash.com/photo-1444418776041-9c7e33cc5a9c',
    'https://images.unsplash.com/photo-1558642452-9d2a7deb7f62',
];


const card =  (product) =>(
    <React.Fragment>
        <CardContent>
            <img
                src={product.imageUrl}
                alt={product.productName}
                style={{ width: "100%", height: "100%" }}
            />
            <br />
            <Typography variant="h5" component="div">
                {product.productName}
            </Typography>
            <Typography sx={{ mb: 1.5 }} color="text.secondary">

            </Typography>
            <Typography variant="body2">
                {product.salesPrice} TL
            </Typography>
        </CardContent>
        <CardActions sx={{ justifyContent: "center", marginBottom: 1 }}>
            <Button onClick={() => {
                addProduct(product.productId);
                toast.success('Product successfully added to Cart')
            }} size="small" variant='contained' sx={{ justifyContent: "center",background: '#4d72eb'}}> Add to Cart </Button>
        </CardActions>
    </React.Fragment>
);

const addProduct = (productId) => {
    if (localStorage.getItem('cartId') == null) {
        createCart().then((res => {
            localStorage.setItem("cartId", res);
        }))
    }
    const cartId = localStorage.getItem('cartId');
    console.log(cartId);
    addProductToCart(cartId, productId).then((res) => console.log("eklendi"));
}

export  const Products = ({products}) => {
    return (

        <Grid container spacing={2}>
            {
                products?.map((product) => {
                    return(
                        <Grid item>
                            <Card sx={{minWidth: 275}} variant="outlined">{card(product)}
                                <img
                                    src={product.imageUrl}
                                    alt={product.productName}
                                    style={{ width: "100%", height: "100%" }}
                                />

                            </Card>

                        </Grid>
                    )
                })
            }
        </Grid>
    );
}
